template.value:
public static int binarySearch(int arr[], int l, int r, int x) 
{ 
    if (r >= l) { 
        int mid = l + (r - l) / 2; 

        if (arr[mid] == x) 
            return mid; 

        // go left
        if (arr[mid] > x) 
            return binarySearch(arr, l, mid - 1, x); 

        // go right
        return binarySearch(arr, mid + 1, r, x); 
    } 

    // We reach here when element is not 
    // present in array 
    return -1; 
}
